/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int maxPathSum(TreeNode root) {
        if (root == null) {
            return 0;
        }

        getMaxSum(root);

        return result;

    }

    private int result = Integer.MIN_VALUE;


    private int getMaxSum(TreeNode node) {
        int leftMax = 0, rightMax = 0;

        if (node.left != null) {
            leftMax = getMaxSum(node.left);
        }

        if (node.right != null) {
            rightMax = getMaxSum(node.right);
        }

        int maxPath = node.val;

        if (leftMax > 0 ) {
            maxPath += leftMax;
        }

        if (rightMax > 0) {
            maxPath += rightMax;
        }

        if (maxPath > result) {
            result = maxPath;
        }
        if (leftMax > rightMax && leftMax > 0) {
            return node.val + leftMax;
        } else if (rightMax > leftMax && rightMax > 0) {
            return node.val + rightMax;
        } else {
            return node.val;
        }
    }
}
