/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        int lA = 0, lB = 0;
        ListNode temp = headA;

        while (temp != null) {
            lA++;
            temp = temp.next;
        }

        temp = headB;

        while (temp != null) {
            lB++;
            temp = temp.next;
        }

        if (lA > lB) {
            for (int i = 0; i < lA - lB; i++) {
                headA = headA.next;
            }
        } else if (lA < lB) {
            for (int i = 0; i < lB - lA; i++) {
                headB = headB.next;
            }
        }

        while (headA != headB) {
            headA = headA.next;
            headB = headB.next;
        }
        return headA;
    }
}
//leetcode submit region end(Prohibit modification and deletion)
