class Solution {
    public int strStr(String haystack, String needle) {
        int lh = haystack.length();
        int ln = needle.length();

        if (lh < ln) {
            return -1;
        }


        if (ln == 0) {
            return 0;
        }

        if (ln == lh) {
            if (haystack.equals(needle)) {
                return 0;
            } else {
