class Solution {
    public void moveZeroes(int[] nums) {
        int slow = 0, fast = 0;

        if (nums == null || nums.length == 0) {
            return;
        }
        while (fast < nums.length) {
            if (nums[fast] == 0) {
                fast++;
            } else {
                nums[slow++] = nums[fast++];
            }
        }
        while (slow < nums.length) {
            nums[slow++] = 0;
        }

    }
}
