//leetcode submit region begin(Prohibit modification and deletion)
class Solution {
    public double myPow(double x, int n) {
        int absN = n > 0 ? n : -n;

        if (n == Integer.MIN_VALUE) {
            if ((int)x == 1 || (int)x == -1) {
                return 1.0;
            } else {
                return 0.0;
            }
        }

        double result = 1.0;
        double curP = x;

        while (absN > 0) {
            if (absN % 2 == 1) {
                result *= curP;
            }

            absN = absN >> 1;
            curP = curP * curP;
        }

        if (n > 0) {
            return result;
        } else {
            return 1.0 / result;
        }
    }
}
//leetcode submit region end(Prohibit modification and deletion)
```

