class Solution {
    public int[] exchange(int[] nums) {
        if (nums == null || nums.length == 0) {
            return nums;
        }

        int start = 0, end = nums.length - 1;

        while (start < end) {
            boolean doSwap = nums[start] % 2 == 0 && nums[end] % 2 == 1;
            if (doSwap) {
                int temp = nums[start];
                nums[start] = nums[end];
                nums[end] = temp;
                start++;
                end--;
            } else {
                if (nums[start] % 2 == 1) {
                    start++;
                }
                if (nums[end] % 2 == 0) {
                    end--;
                }
            }
        }

        return nums;
    }
}
