                if ("null".equals(rightV)) {
                    node.right = null;
                } else {
                    TreeNode rightN = new TreeNode(Integer.valueOf(rightV));
                    node.right = rightN;
                    nodeQueue.add(rightN);
                }

            }
        }

        return root;
    }
