            int curNum = numStr.charAt(i) - '0';
            if (i == 0) {
                f[i] = 1;
            } else {
                if (lastNum * 10 + curNum >= 10 && lastNum * 10 + curNum <= 25) {
                    if (i == 1) {
                        f[i] = 2;
                    } else {
                        f[i] = f[i - 1] + f[i - 2];
                    }
                } else {
                    f[i] = f[i - 1];
                }
            }
            lastNum = curNum;
        }
        return f[numStr.length() - 1];
    }
}
