    public int[] singleNumbers(int[] nums) {
        int twoXor = 0;

        for (int num : nums) {
            twoXor ^= num;
        }

        int key = 1;

        while ((key & twoXor) == 0) {
            key = key << 1;
        }

        int numOne = 0, numTwo = 0;

        for (int num : nums) {
            if ((num & key) == 0) {
                numOne ^= num;
            } else {
                numTwo ^= num;
            }
        }

        int[] result = new int[2];
        result[0] = numOne;
        result[1] = numTwo;

        return result;
    }
}
