class Solution {
    public int divide(int a, int b) {
        // 
        if (a == Integer.MIN_VALUE) {
            if (b == 1) {
                return Integer.MIN_VALUE;
            }
            if (b == -1) {
                return Integer.MAX_VALUE;
            }
        }
        // 
        if (b == Integer.MIN_VALUE) {
            return a == Integer.MIN_VALUE ? 1 : 0;
        }
        //  0 
        if (a == 0) {
            return 0;
        }

        // 
        // 
        boolean rev = false;
        if (a > 0) {
            a = -a;
            rev = !rev;
        }
        if (b > 0) {
            b = -b;
            rev = !rev;
        }

        int left = 1, right = Integer.MAX_VALUE, ans = 0;
        while (left <= right) {
            // 
            int mid = left + ((right - left) >> 1);
            boolean check = quickAdd(b, mid, a);
            if (check) {
                ans = mid;
                // 
                if (mid == Integer.MAX_VALUE) {
                    break;
                }
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }

        return rev ? -ans : ans;
    }

    // 
    public boolean quickAdd(int y, int z, int x) {
        // x  y z 
        //  z * y >= x 
        int result = 0, add = y;
        while (z != 0) {
            if ((z & 1) != 0) {
                //  result + add >= x
                if (result < x - add) {
                    return false;
                }
                result += add;
            }
            if (z != 1) {
                //  add + add >= x
                if (add < x - add) {
                    return false;
                }
                add += add;
            }
            // 
            z >>= 1;
        }
        return true;
    }
}
```

