                resutSet.add(resultKey);
                List<Integer> oneList = new ArrayList<>();
                oneList.add(nums[start]);
                oneList.add(nums[end]);
                oneList.add(-target);
                result.add(oneList);
                start++;
            } else if (nums[start] + nums[end] < target) {
                start++;
            } else {
                end--;
            }
        }
    }
}
