            for (int j = i % 2 == 1 ? 1 : 0; j < i; j+=2) {
                if ((i - j) <= result) {
                    break;
                }
                if ((i - j) / 2 == ones[i] - ones[j] && (i - j) > result) {
                    result = i - j;
                    break;
                }
            }
        }

        return result;
    }
}
