//
// 
// 1 <= nums.length <= 5 * 105 
// -231 <= nums[i] <= 231 - 1 
// 
// Related Topics Array Hash Table 
//  10299  1387



//leetcode submit region begin(Prohibit modification and deletion)
class Solution {
    public int firstMissingPositive(int[] nums) {
        Set<Integer> treeSet = new TreeSet<>();
        for (int num : nums) {
            if (num > 0) {
                treeSet.add(num);
            }
        }

        int last = 0;

        for (Integer value : treeSet) {
            if (value - last > 1) {
                return last + 1;
            }
            last = value;
        }

        return last + 1;
    }
}
